import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import withLayout from 'nextra-theme-docs'
import { withSSG } from 'nextra/ssg'
import layoutConfig from '/Users/halves/Developer/templates/jetpack-stack/docs/theme.config.js'
import Callout from 'nextra-theme-docs/callout'

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const MDXLayout = function NextraPage (props) {
    return withSSG(withLayout({
      filename: "get-started.mdx",
      route: "/api/get-started",
      meta: {},
      pageMap: [{"name":"_app","route":"/_app"},{"name":"api","children":[{"name":"anchors","route":"/api/anchors"},{"name":"get-started","route":"/api/get-started"},{"name":"i18n","route":"/api/i18n"},{"name":"mdx","route":"/api/mdx"},{"name":"meta.json","meta":{"get-started":"Get Started","structure":"Structure","mdx":"Markdown and React","ssg":"SSG Support","anchors":"Anchor Links","i18n":"I18n"}},{"name":"ssg","route":"/api/ssg"},{"name":"structure","route":"/api/structure"}],"route":"/api"},{"name":"docs","children":[{"name":"anchors","route":"/docs/anchors"},{"name":"get-started","route":"/docs/get-started"},{"name":"i18n","route":"/docs/i18n"},{"name":"mdx","route":"/docs/mdx"},{"name":"meta.json","meta":{"get-started":"Get Started","structure":"Structure","mdx":"Markdown and React","ssg":"SSG Support","anchors":"Anchor Links","i18n":"I18n"}},{"name":"ssg","route":"/docs/ssg"},{"name":"structure","route":"/docs/structure"}],"route":"/docs"},{"name":"index","route":"/"},{"name":"meta.json","meta":{"index":"Jetpack","api":"Api","docs":"Docs"}}]
    }, layoutConfig))(props)
}
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...props} components={components} mdxType="MDXLayout">
    <h1>{`Get Started`}</h1>
    <p>{`It has never been easier to create the routes and organize the sidebar structure!`}</p>
    <ol>

      <li {...{
        "parentName": "ol"
      }}>

        <p {...{
          "parentName": "li"
        }}>{`Install Next.js and React: `}
          <inlineCode {...{
            "parentName": "p"
          }}>{`yarn add next react react-dom`}</inlineCode></p>

      </li>


      <li {...{
        "parentName": "ol"
      }}>

        <p {...{
          "parentName": "li"
        }}>{`Install Nextra and the docs theme: `}
          <inlineCode {...{
            "parentName": "p"
          }}>{`yarn add nextra nextra-theme-docs`}</inlineCode></p>

      </li>


      <li {...{
        "parentName": "ol"
      }}>

        <p {...{
          "parentName": "li"
        }}>{`Create the following Next.js config and theme config under the root directory:`}</p>

      </li>

    </ol>
    <pre><code {...{
        "className": "language-jsx",
        "parentName": "pre"
      }}>{`// next.config.js
const withNextra = require('nextra')('nextra-theme-docs', './theme.config.js')
module.exports = withNextra()
`}</code></pre>
    <pre><code {...{
        "className": "language-jsx",
        "parentName": "pre"
      }}>{`// theme.config.js
export default {
  repository: 'https://github.com/shuding/nextra', // project repo
  docsRepository: 'https://github.com/shuding/nextra', // docs repo
  branch: 'master', // branch of docs
  path: '/', // path of docs
  titleSuffix: ' – Nextra',
  nextLinks: true,
  prevLinks: true,
  search: true,
  customSearch: null, // customizable, you can use algolia for example
  darkMode: true,
  footer: true,
  footerText: 'MIT 2020 © Shu Ding.',
  footerEditOnGitHubLink: true, // will link to the docs repo
  logo: <>
    <svg>...</svg>
    <span>Next.js Static Site Generator</span>
  </>,
  head: <>
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <meta name="description" content="Nextra: the next docs builder" />
    <meta name="og:title" content="Nextra: the next docs builder" />
  </>
}
`}</code></pre>
    <ol {...{
      "start": 4
    }}>

      <li {...{
        "parentName": "ol"
      }}>{`Create `}
        <inlineCode {...{
          "parentName": "li"
        }}>{`pages/_app.js`}</inlineCode>
        {` and include the theme stylesheet:`}</li>

    </ol>
    <pre><code {...{
        "className": "language-jsx",
        "parentName": "pre"
      }}>{`import 'nextra-theme-docs/style.css'

export default function Nextra({ Component, pageProps }) {
  return <Component {...pageProps} />
}
`}</code></pre>
    <ol {...{
      "start": 5
    }}>

      <li {...{
        "parentName": "ol"
      }}>{`You are good to go!`}</li>

    </ol>
    <hr />

    <Callout mdxType="Callout">
      <p>{`Any `}
        <inlineCode {...{
          "parentName": "p"
        }}>{`.md`}</inlineCode>
        {` or `}
        <inlineCode {...{
          "parentName": "p"
        }}>{`.mdx`}</inlineCode>
        {` file will turn into a doc page and be displayed in sidebar. You can also create a `}
        <inlineCode {...{
          "parentName": "p"
        }}>{`meta.json`}</inlineCode>
        {` file to customize the page order and title.`}</p>
      <p>{`Check the source code: `}
        <a {...{
          "href": "https://github.com/shuding/nextra",
          "parentName": "p"
        }}>{`https://github.com/shuding/nextra`}</a>
        {` for more information.`}</p>
    </Callout>
    <Callout mdxType="Callout">
      <p>{`You can also use `}
        <a {...{
          "href": "https://nextjs.org/docs/basic-features/built-in-css-support#css-in-js",
          "parentName": "p"
        }}><inlineCode {...{
            "parentName": "a"
          }}>{`<style jsx>`}</inlineCode></a>
        {` to style elements inside `}
        <inlineCode {...{
          "parentName": "p"
        }}>{`theme.config.js`}</inlineCode>
        {`.`}</p>
    </Callout>

  </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
